mapper.select:
select * from (select rownum as rnum, b.* from (select board_no, board_tag, board_title, board_content, board_thumb, read_count, (case when(trunc((sysdate - reg_date)*24*60)) < 60 then(trunc((sysdate - reg_date)*24*60))||'분전' when(trunc((sysdate - reg_date)*24)) < 24 then(trunc((sysdate - reg_date)*24))||'시간전' when trunc((sysdate - reg_date)) < 30 then trunc((sysdate - reg_date)) || '일전' when trunc((sysdate - reg_date) / 30) < 12 then trunc((sysdate - reg_date) / 30) || '개월전' else trunc((sysdate - reg_date) / 365) || '년전' end)as reg_date, member_nickname, (select count(*) from board_like where board_like.board_no = board.board_no) like_count from board
where board_tag = #{tag}
order by board_no desc) b ) where rnum between #{start} and #{end}
