mapper.select:
select count(*) from inquiry




				where inquiry_title like '%'||#{keyword}||'%' or inquiry_content like '%'||#{keyword}||'%' or member_email like '%'||#{keyword}||'%'


				where inquiry_title like '%'||#{keyword}||'%'


				where member_email like '%'||#{keyword}||'%'


				where inquiry_title like '%'||#{keyword}||'%' or inquiry_content like '%'||#{keyword}||'%'


				where inquiry_content like '%'||#{keyword}||'%'






				where (inquiry_title like '%'||#{keyword}||'%' or inquiry_content like '%'||#{keyword}||'%' or member_email like '%'||#{keyword}||'%')and inquiry_type=#{option}


				where inquiry_title like '%'||#{keyword}||'%' and inquiry_type=#{option}


				where member_email like '%'||#{keyword}||'%' and inquiry_type=#{option}


				where (inquiry_title like '%'||#{keyword}||'%' or inquiry_content like '%'||#{keyword}||'%'}) and inquiry_type=#{option}


				where inquiry_content like '%'||#{keyword}||'%' and inquiry_type=#{option}

select * from (select rownum as rnum, b.* from(SELECT inquiry_no,inquiry_title,to_char(inquiry_reg_date)as inquiry_reg_date,inquiry_type, member_email from inquiry 




				where inquiry_title like '%'||#{keyword}||'%' or inquiry_content like '%'||#{keyword}||'%' or member_email like '%'||#{keyword}||'%'


				where inquiry_title like '%'||#{keyword}||'%'


				where member_email like '%'||#{keyword}||'%'


				where inquiry_title like '%'||#{keyword}||'%' or inquiry_content like '%'||#{keyword}||'%'


				where inquiry_content like '%'||#{keyword}||'%'






				where (inquiry_title like '%'||#{keyword}||'%' or inquiry_content like '%'||#{keyword}||'%' or member_email like '%'||#{keyword}||'%')and inquiry_type=#{option}


				where inquiry_title like '%'||#{keyword}||'%' and inquiry_type=#{option}


				where member_email like '%'||#{keyword}||'%' and inquiry_type=#{option}


				where (inquiry_title like '%'||#{keyword}||'%' or inquiry_content like '%'||#{keyword}||'%') and inquiry_type=#{option}


				where inquiry_content like '%'||#{keyword}||'%' and inquiry_type=#{option}




order by inquiry_no desc)b) where (rnum BETWEEN #{start} and #{end})
